import React, { useState } from "react";
import './App.css';
import logo from './dnd-logo.svg';
import Creature from './components/Creature';

function App() {
  const [dragId, setDragId] = useState();

  const [creatures, setCreatures] = useState([
    {
      id: '1',
      npc: false,
      name: "Jellifer",
      pc: "Alison",
      level: 2,
      hp: 12
    }
  ]);


  const handleDrag = (ev) => {
    setDragId(ev.currentTarget.id);
  };

  const handleDrop = (ev) => {
  const dragCreature = creatures.find((creature) => creature.id === dragId);
  const dropCreature = creatures.find((creature) => creature.id === ev.currentTarget.id);

    const dragCreatureOrder = dragCreature.order;
    const dropCreatureOrder = dropCreature.order;

    const newCreatureState = creatures.map((creature) => {
      if (creature.id === dragId) {
        creature.order = dropCreatureOrder;
      }
      if (creature.id === ev.currentTarget.id) {
        creature.order = dragCreatureOrder;
      }
      return creature;
    });

    setCreatures(newCreatureState);
  };

  const newCreatureState = creatures.map((creature) => {
      if (creature.id === dragId) {
        creature.order = dropCreatureOrder;
      }
      if (creature.id === ev.currentTarget.id) {
        creature.order = dragCreatureOrder;
      }
      return creature;
    });

  return (
    <div className="App">
      <header className="p-5 bg-black text-white">
        <img src={logo} className="w-20" alt='logo'/>
      </header>
      <main className="p-4 grid grid-cols-2 gap-8">
        <section className="grid gap-4">
          {creatures
              .sort((a, b) => a.order - b.order)
              .map((creature) => (
                <Creature
                  key = {creature.id}
                  npc = {creature.npc}
                  name = {creature.name}
                  pc = {creature.pc}
                  level = {creature.level}
                  hp = {creature.hp}
                />
              ))}
          <Creature
            npc={false}
            name="Jellifer"
            pc="Alison"
            level={2}
            hp={12}
          />
          <Creature
            npc={false}
            name="Josephine"
            pc="Kendall"
            level={2}
            hp={6}
          />
          <Creature
            npc={true}
            name="Kobold"
            level={2}
            hp={6}
          />
        </section>
        <section className="border border-blue-500 p-4">
          <h2>goodbye</h2>
        </section>
      </main>
    </div>
  );
}

export default App;
