          case 'won':
            return <Won handleClick={handleClick} />
          case 'lost':
            return <Lost handleClick={handleClick} />
          default:
            return null
        }

  return (
    <span className="block mx-auto w-8 text-sm bg-slate-500 text-white rounded-lg font-bold">{bonus}</span>
  )
}

export default Bonus;
