import React, { useState } from "react";
import './App.css';
import logo from './dnd-logo.svg';
import Creature from './components/Creature';
import { Draggable } from "react-drag-reorder";


function App() {
  const [creatures] = useState([
    {
      id: '1',
      npc: false,
      name: "Jellifer",
      pc: "Alison",
      level: 2,
      hp: 12,
      str: 14,
      strBonus: 2,
      dex: 8,
      dexBonus: -1
    },
    {
      id: '2',
      npc: false,
      name: "Josephine",
      pc: "Kendall",
      level: 2,
      hp: 10,
      str: 14,
      strBonus: 2,
      dex: 8,
      dexBonus: -1,
      const: 15,
      constBonus: 2,
      int: 10,
    },
    {
      id: '3',
      npc: true,
      name: "Kobald",
      pc: "X",
      level: 1,
      hp: 5
    }
  ]);


  return (
    <div className="App">
      <header className="p-5 bg-black text-white">
        <img src={logo} className="w-20" alt='logo'/>
      </header>
      <main className="p-4 grid grid-cols-2 gap-8">
        <section className="grid gap-4">
          <Draggable>
          {creatures
              .sort((a, b) => a.order - b.order)
              .map((creature) => (
                <Creature
                  key = {creature.id}
                  npc = {creature.npc}
                  name = {creature.name}
                  pc = {creature.pc}
                  level = {creature.level}
                  hp = {creature.hp}
                  str = {creature.str}
                  strBonus = {creature.strBonus}
                  dex = {creature.dex}
                  dexBonus = {creature.dexBonus}
                  const = {creature.const}
                  constBonus = {creature.constBonus}
                  int = {creature.int}
                  intBonus = {creature.intBonus}
                />
              ))}
          </Draggable>
        </section>
        <section className="border border-blue-500 p-4">
          <h2>Cheat sheets</h2>
        </section>
      </main>
    </div>
  );
}

export default App;
