import React, {useState, useCallback} from 'react';

function Creature(props) {

    const [count, setCount]=useState(props.hp);
    const OnIncrementClick = useCallback((e) => {
        setCount(count+1);
      }, [count]);

      const OnDecrementClick = useCallback((e) => {
        setCount(count-1);
    }, [count]);

  return (
    <section className="border border-red-500 font-sans">
      <header className={`${props.npc ? 'bg-red-900': 'bg-black'} p-2 flex justify-between gap-4 text-white text-xs font-bold`}>
        <h2>{props.name} ({props.pc})</h2>
        <h3>Lvl {props.level}</h3>
      </header>
      <main className="p-2 flex justify-between">
        <div className="border border-blue-500">
          <p>stat block</p>
        </div>
        <div className="border border-red-500 text-center w-24">
          <h2 className="text-5xl font-bold mb-4 font-display">{count}</h2>
          <section className="flex justify-between">
            <div onClick={OnIncrementClick} className="cursor-pointer select-none border border-gray-300 w-8 h-8 flex align-center justify-center leading-7 rounded bg-gray-100 hover:border-slate-500 transition-all">+</div>
            <div onClick={OnDecrementClick} className="cursor-pointer select-none border border-gray-300 w-8 h-8 flex align-center justify-center leading-7 rounded bg-gray-100 hover:border-slate-500 transition-all">-</div>
          </section>
        </div>
      </main>
    </section>
  )
}

export default Creature;
