import React, {useState, useCallback} from 'react';

function Creature() {
  const [count, setCount]=useState(0);

    const OnIncrementClick = useCallback((e) => {
        setCount(count+1);
      }, [count]);

      const OnDecrementClick = useCallback((e) => {
        setCount(count-1);
    }, [count]);

  return (
    <section className="border border-red-500">
      <header className="p-4 flex justify-between gap-4 text-white bg-black font-bold">
        <h2>Josephine (Kendall)</h2>
        <h3>Lvl 2</h3>
      </header>
      <main className="p-4">
        <h2 className="text-5xl font-bold">{count}</h2>
        <div onClick={OnIncrementClick} className="select-none p-1 border border-slate-500 w-10 text-center">+</div>
        <div onClick={OnDecrementClick} className="select-none">-</div>
      </main>
    </section>
  )
}

export default Creature;
